class MinStack:
  def __init__(self):
    self.stack = []
  def push(self, x: int) -> None:
    mini = x if not self.stack else min(self.stack[-1][1], x)
    self.stack.append([x, mini])
  def pop(self) -> None:
    self.stack.pop()
  def top(self) -> int:
    return self.stack[-1][0]
  def getMin(self) -> int:
    return self.stack[-1][1]

