    newHead = self.reverseList(head.next)
    head.next.next = head
    head.next = None
    return newHead

from enum import Enum

 class State(Enum):
  kInit = 0
  kVisiting = 1
  kVisited = 2

 class Solution:
  def canFinish(self, numCourses: int, prerequisites: List[List[int]]) -> bool:
    graph = [[] for _ in range(numCourses)]
    state = [State.kInit] * numCourses
    for a, b in prerequisites:
      graph[b].append(a)
    def hasCycle(u: int) -> bool:
      if state[u] == State.kVisiting:
        return True
      if state[u] == State.kVisited:
        return False
      state[u] = State.kVisiting
      if any(hasCycle(v) for v in graph[u]):
        return True
      state[u] = State.kVisited
      return False
    return not any(hasCycle(i) for i in range(numCourses))

class TrieNode:
  def __init__(self):
    self.children: Dict[str, TrieNode] = collections.defaultdict(TrieNode)
    self.isWord = False

 class Trie:
  def __init__(self):
    self.root = TrieNode()
  def insert(self, word: str) -> None:
    node: TrieNode = self.root
    for c in word:
      if c not in node.children:
        node.children[c] = TrieNode()
      node = node.children[c]
    node.isWord = True
  def search(self, word: str) -> bool:
    node: TrieNode = self._find(word)
    return node and node.isWord
  def startsWith(self, prefix: str) -> bool:
    return self._find(prefix)
  def _find(self, prefix: str) -> Optional[TrieNode]:
    node: TrieNode = self.root
    for c in prefix:
      if c not in node.children:
        return None
      node = node.children[c]
    return node

class Solution:
  def minSubArrayLen(self, s: int, nums: List[int]) -> int:
    ans = math.inf
    summ = 0
    j = 0
    for i, num in enumerate(nums):
      summ += num
      while summ >= s:
        ans = min(ans, i - j + 1)
        summ -= nums[j]
        j += 1
    return ans if ans != math.inf else 0

from enum import Enum

 class State(Enum):
  kInit = 0
  kVisiting = 1
  kVisited = 2

 class Solution:
  def findOrder(self, numCourses: int, prerequisites: List[List[int]]) -> List[int]:
    ans = []
    graph = [[] for _ in range(numCourses)]
    state = [State.kInit] * numCourses
    for v, u in prerequisites:
      graph[u].append(v)
    def hasCycle(u: int) -> bool:
      if state[u] == State.kVisiting:
        return True
      if state[u] == State.kVisited:
        return False
      state[u] = State.kVisiting
      if any(hasCycle(v) for v in graph[u]):
        return True
      state[u] = State.kVisited
      ans.append(u)
      return False
    if any(hasCycle(i) for i in range(numCourses)):
      return []
    return ans[::-1]

class TrieNode:
  def __init__(self):
    self.children: Dict[str, TrieNode] = collections.defaultdict(TrieNode)
    self.isWord = False

 class WordDictionary:
  def __init__(self):
    self.root = TrieNode()
  def addWord(self, word: str) -> None:
    node: TrieNode = self.root
    for c in word:
      if c not in node.children:
        node.children[c] = TrieNode()
      node = node.children[c]
    node.isWord = True
  def search(self, word: str) -> bool:
    return self._dfs(word, 0, self.root)
  def _dfs(self, word: str, s: int, node: TrieNode) -> bool:
    if s == len(word):
      return node.isWord
    if word[s] != '.':
      next: TrieNode = node.children[word[s]]
      return self._dfs(word, s + 1, next) if next else False
    for c in string.ascii_lowercase:
      if c in node.children and self._dfs(word, s + 1, node.children[c]):
        return True
    return False

class TrieNode:
  def __init__(self):
    self.children: Dict[str, TrieNode] = collections.defaultdict(TrieNode)
    self.word: Optional[str] = None

 class Solution:
  def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
    m = len(board)
    n = len(board[0])
    ans = []
    root = TrieNode()
    def insert(word: str) -> None:
      node = root
      for c in word:
        if c not in node.children:
          node.children[c] = TrieNode()
        node = node.children[c]
      node.word = word
    for word in words:
      insert(word)
    def dfs(i: int, j: int, node: TrieNode) -> None:
      if i < 0 or i == m or j < 0 or j == n:
        return
      if board[i][j] == '*':
        return
      c = board[i][j]
      if c not in node.children:
        return
      child = node.children[c]
      if child.word:
        ans.append(child.word)
        child.word = None
      board[i][j] = '*'
      dfs(i + 1, j, child)
      dfs(i - 1, j, child)
      dfs(i, j + 1, child)
      dfs(i, j - 1, child)
      board[i][j] = c
    for i in range(m):
      for j in range(n):
        dfs(i, j, root)
    return ans

class Solution:
  def rob(self, nums: List[int]) -> int:
    if not nums:
      return 0
    if len(nums) < 2:
      return nums[0]
    def rob(l: int, r: int) -> int:
      dp1 = 0
      dp2 = 0
      for i in range(l, r + 1):
        temp = dp1
        dp1 = max(dp1, dp2 + nums[i])
        dp2 = temp
      return dp1
    return max(rob(0, len(nums) - 2),
               rob(1, len(nums) - 1))

