class Solution:
  def threeSumSmaller(self, nums: List[int], target: int) -> int:
    if len(nums) < 3:
      return 0
    ans = 0
    nums.sort()
    for i in range(len(nums) - 2):
      l = i + 1
      r = len(nums) - 1
      while l < r:
        if nums[i] + nums[l] + nums[r] < target:
          ans += r - l
          l += 1
        else:
          r -= 1
    return ans

class Solution:
  def singleNumber(self, nums: List[int]) -> List[int]:
    xors = functools.reduce(operator.xor, nums)
    lowbit = xors & -xors
    ans = [0, 0]
    for num in nums:
      if num & lowbit:
        ans[0] ^= num
      else:
        ans[1] ^= num
    return ans

class Solution:
  def validTree(self, n: int, edges: List[List[int]]) -> bool:
    if n == 0 or len(edges) != n - 1:
      return False
    graph = [[] for _ in range(n)]
    q = collections.deque([0])
    seen = {0}
    for u, v in edges:
      graph[u].append(v)
      graph[v].append(u)
    while q:
      u = q.popleft()
      for v in graph[u]:
        if v not in seen:
          q.append(v)
          seen.add(v)
    return len(seen) == n

class Solution:
  def isUgly(self, n: int) -> bool:
    if n == 0:
      return False
    for prime in 2, 3, 5:
      while n % prime == 0:
        n //= prime
    return n == 1

class Solution:
  def nthUglyNumber(self, n: int) -> int:
    nums = [1]
    i2 = 0
    i3 = 0
    i5 = 0
    while len(nums) < n:
      next2 = nums[i2] * 2
      next3 = nums[i3] * 3
      next5 = nums[i5] * 5
      next = min(next2, next3, next5)
      if next == next2:
        i2 += 1
      if next == next3:
        i3 += 1
      if next == next5:
        i5 += 1
      nums.append(next)
    return nums[-1]

class Solution:
  def canPermutePalindrome(self, s: str) -> bool:
    seen = set()
    for c in s:
      if c in seen:
        seen.remove(c)
      else:
        seen.add(c)
