class Solution:
  def generatePalindromes(self, s: str) -> List[str]:
    count = collections.Counter(s)
    odd = sum(value & 1 for value in count.values())
    if odd > 1:
      return []
    ans = []
    candidates = []
    mid = ''
    for key, value in count.items():
      if value & 1:
        mid += key
      for _ in range(value // 2):
        candidates.append(key)
    def dfs(used: List[bool], path: List[chr]) -> None:
      if len(path) == len(candidates):
        ans.append(''.join(path) + mid + ''.join(path[::-1]))
        return
      for i, candidate in enumerate(candidates):
        if used[i]:
          continue
        if i > 0 and candidate == candidates[i - 1] and not used[i - 1]:
          continue
        used[i] = True
        path.append(candidate)
        dfs(used, path)
        path.pop()
        used[i] = False
    dfs([False] * len(candidates), [])
    return ans

class Solution:
  def missingNumber(self, nums: List[int]) -> int:
    ans = len(nums)
    for i, num in enumerate(nums):
      ans ^= i ^ num
    return ans

class Solution:
  def alienOrder(self, words: List[str]) -> str:
    graph = {}
    inDegree = [0] * 26
    self._buildGraph(graph, words, inDegree)
    return self._topology(graph, inDegree)
  def _buildGraph(self, graph: Dict[chr, Set[chr]], words: List[str], inDegree: List[int]) -> None:
    for word in words:
      for c in word:
        if c not in graph:
          graph[c] = set()
    for first, second in zip(words, words[1:]):
      length = min(len(first), len(second))
      for j in range(length):
        u = first[j]
        v = second[j]
        if u != v:
          if v not in graph[u]:
            graph[u].add(v)
            inDegree[ord(v) - ord('a')] += 1
          break
        if j == length - 1 and len(first) > len(second):
          graph.clear()
          return
  def _topology(self, graph: Dict[chr, Set[chr]], inDegree: List[int]) -> str:
    s = ''
    q = collections.deque()
    for c in graph:
      if inDegree[ord(c) - ord('a')] == 0:
        q.append(c)
    while q:
      u = q.pop()
      s += u
      for v in graph[u]:
        inDegree[ord(v) - ord('a')] -= 1
        if inDegree[ord(v) - ord('a')] == 0:
          q.append(v)
    return s if len(s) == len(graph) else ''

class Solution:
  def closestValue(self, root: Optional[TreeNode], target: float) -> int:
    if target < root.val and root.left:
      left = self.closestValue(root.left, target)
      if abs(left - target) < abs(root.val - target):
        return left
    if target > root.val and root.right:
      right = self.closestValue(root.right, target)
      if abs(right - target) < abs(root.val - target):
        return right
    return root.val

class Codec:
  def encode(self, strs: List[str]) -> str:
    return ''.join(str(len(s)) + '/' + s for s in strs)
  def decode(self, s: str) -> List[str]:
    decoded = []
    i = 0
    while i < len(s):
      slash = s.find('/', i)
      length = int(s[i:slash])
      i = slash + length + 1
      decoded.append(s[slash + 1:i])
    return decoded

class Solution:
  def closestKValues(self, root: Optional[TreeNode], target: float, k: int) -> List[int]:
    q = collections.deque()
    def inorder(root: Optional[TreeNode]) -> None:
      if not root:
        return
      inorder(root.left)
      q.append(root.val)
      inorder(root.right)
    inorder(root)
    while len(q) > k:
      if abs(q[0] - target) > abs(q[-1] - target):
        q.popleft()
      else:
        q.pop()
    return list(q)

class Solution:
  def numberToWords(self, num: int) -> str:
    if num == 0:
      return "Zero"
    belowTwenty = ["",        "One",       "Two",      "Three",
                   "Four",    "Five",      "Six",      "Seven",
                   "Eight",   "Nine",      "Ten",      "Eleven",
                   "Twelve",  "Thirteen",  "Fourteen", "Fifteen",
                   "Sixteen", "Seventeen", "Eighteen", "Nineteen"]
    tens = ["",      "Ten",   "Twenty",  "Thirty", "Forty",
            "Fifty", "Sixty", "Seventy", "Eighty", "Ninety"]
    def helper(num: int) -> str:
      if num < 20:
        s = belowTwenty[num]
      elif num < 100:
        s = tens[num // 10] + " " + belowTwenty[num % 10]
      elif num < 1000:
        s = helper(num // 100) + " Hundred " + helper(num % 100)
      elif num < 1000000:
        s = helper(num // 1000) + " Thousand " + helper(num % 1000)
      elif num < 1000000000:
        s = helper(num // 1000000) + " Million " + \
            helper(num % 1000000)
      else:
        s = helper(num // 1000000000) + " Billion " + \
            helper(num % 1000000000)
      return s.strip()
    return helper(num)

class Solution:
  def hIndex(self, citations: List[int]) -> int:
    n = len(citations)
    accumulate = 0
    count = [0] * (n + 1)
    for citation in citations:
      count[min(citation, n)] += 1
    for i, c in reversed(list(enumerate(count))):
      accumulate += c
      if accumulate >= i:
        return i

class Solution:
  def hIndex(self, citations: List[int]) -> int:
    l = 0
    r = len(citations)
    while l < r:
      m = (l + r) // 2
      if citations[m] >= len(citations) - m:
        r = m
      else:
        l = m + 1
    return len(citations) - l

class Solution:
  def numWays(self, n: int, k: int) -> int:
    if n == 0:
      return 0
    if n == 1:
      return k
    if n == 2:
      return k * k
    dp = [0] * (n + 1)
    dp[0] = 0
    dp[1] = k
    dp[2] = k * k
    for i in range(3, n + 1):
      dp[i] = (dp[i - 1] + dp[i - 2]) * (k - 1)
    return dp[n]

class Solution:
  def firstBadVersion(self, n: int) -> int:
    l = 1
    r = n
    while l < r:
      m = (l + r) >> 1
      if isBadVersion(m):
        r = m
      else:
        l = m + 1
    return l

class Solution:
  def numSquares(self, n: int) -> int:
    dp = [n] * (n + 1)
    dp[0] = 0
    dp[1] = 1
    for i in range(2, n + 1):
      j = 1
      while j * j <= i:
        dp[i] = min(dp[i], dp[i - j * j] + 1)
        j += 1
    return dp[n]

class Solution:
  def wiggleSort(self, nums: List[int]) -> None:
    for i in range(1, len(nums)):
      if not (i & 1) and nums[i] > nums[i - 1] or (i & 1) and nums[i] < nums[i - 1]:
        nums[i], nums[i - 1] = nums[i - 1], nums[i]

class ZigzagIterator:
  def __init__(self, v1: List[int], v2: List[int]):
    def vals():
      for i in itertools.count():
        for v in v1, v2:
          if i < len(v):
            yield v[i]
    self.vals = vals()
    self.n = len(v1) + len(v2)
  def next(self):
    self.n -= 1
    return next(self.vals)
  def hasNext(self):
    return self.n > 0

class Solution:
  def addOperators(self, num: str, target: int) -> List[str]:
    ans = []
    def dfs(start: int, prev: int, eval: int, path: List[str]) -> None:
      if start == len(num):
        if eval == target:
          ans.append(''.join(path))
        return
      for i in range(start, len(num)):
        if i > start and num[start] == '0':
          return
        s = num[start:i + 1]
        curr = int(s)
        if start == 0:
          path.append(s)
          dfs(i + 1, curr, curr, path)
          path.pop()
        else:
          for op in ['+', '-', '*']:
            path.append(op + s)
            if op == '+':
              dfs(i + 1, curr, eval + curr, path)
            elif op == '-':
              dfs(i + 1, -curr, eval - curr, path)
            else:
              dfs(i + 1, prev * curr, eval - prev + prev * curr, path)
            path.pop()
    dfs(0, 0, 0, [])
    return ans

class Solution:
  def moveZeroes(self, nums: List[int]) -> None:
    j = 0
    for num in nums:
      if num != 0:
        nums[j] = num
        j += 1
    for i in range(j, len(nums)):
      nums[i] = 0

class PeekingIterator:
  def __init__(self, iterator: Iterator):
    self.iterator = iterator
    self.buffer = self.iterator.next() if self.iterator.hasNext() else None
  def peek(self) -> int:
    return self.buffer
  def next(self) -> int:
    next = self.buffer
    self.buffer = self.iterator.next() if self.iterator.hasNext() else None
    return next
  def hasNext(self) -> bool:
    return self.buffer is not None

class Solution:
  def inorderSuccessor(self, root: Optional[TreeNode], p: Optional[TreeNode]) -> Optional[TreeNode]:
    if not root:
      return None
    if root.val <= p.val:
      return self.inorderSuccessor(root.right, p)
    return self.inorderSuccessor(root.left, p) or root

class Solution:
  def wallsAndGates(self, rooms: List[List[int]]) -> None:
    kInf = 2**31 - 1
    m = len(rooms)
    n = len(rooms[0])
    dirs = [0, 1, 0, -1, 0]
    q = collections.deque()
    for i in range(m):
      for j in range(n):
        if rooms[i][j] == 0:
          q.append((i, j))
    while q:
      i, j = q.popleft()
      for k in range(4):
        x = i + dirs[k]
        y = j + dirs[k + 1]
        if x < 0 or x == m or y < 0 or y == n:
          continue
        if rooms[x][y] != kInf:
          continue
        rooms[x][y] = rooms[i][j] + 1
        q.append((x, y))

class Solution:
  def findDuplicate(self, nums: List[int]) -> int:
    slow = nums[nums[0]]
    fast = nums[nums[nums[0]]]
    while slow != fast:
      slow = nums[slow]
      fast = nums[nums[fast]]
    slow = nums[0]
    while slow != fast:
      slow = nums[slow]
      fast = nums[fast]
    return slow

class ValidWordAbbr:
  def __init__(self, dictionary: List[str]):
    self.dict = set(dictionary)
    self.abbrUnique = {}
    for word in self.dict:
      abbr = self._getAbbr(word)
      self.abbrUnique[abbr] = abbr not in self.abbrUnique
  def isUnique(self, word: str) -> bool:
    abbr = self._getAbbr(word)
    return abbr not in self.abbrUnique or self.abbrUnique[abbr] and word in self.dict
  def _getAbbr(self, s: str) -> str:
    n = len(s)
    if n <= 2:
      return s
    return s[0] + str(n - 2) + s[-1]

class Solution:
  def gameOfLife(self, board: List[List[int]]) -> None:
    m = len(board)
    n = len(board[0])
    for i in range(m):
      for j in range(n):
        ones = 0
        for x in range(max(0, i - 1), min(m, i + 2)):
          for y in range(max(0, j - 1), min(n, j + 2)):
            ones += board[x][y] & 1
        if board[i][j] == 1 and (ones == 3 or ones == 4):
          board[i][j] |= 0b10
        if board[i][j] == 0 and ones == 3:
          board[i][j] |= 0b10
    for i in range(m):
      for j in range(n):
        board[i][j] >>= 1

class Solution:
  def wordPattern(self, pattern: str, str: str) -> bool:
    t = str.split()
    return [*map(pattern.index, pattern)] == [*map(t.index, t)]

class Solution:
  def wordPatternMatch(self, pattern: str, s: str) -> bool:
    def isMatch(i: int, j: int, charToString: Dict[chr, str], seen: Set[str]) -> bool:
      if i == len(pattern) and j == len(s):
        return True
      if i == len(pattern) or j == len(s):
        return False
      c = pattern[i]
      if c in charToString:
        t = charToString[c]
        if t not in s[j:]:
          return False
        return isMatch(i + 1, j + len(t), charToString, seen)
      for k in range(j, len(s)):
        t = s[j:k + 1]
        if t in seen:
          continue
        charToString[c] = t
        seen.add(t)
        if isMatch(i + 1, k + 1, charToString, seen):
          return True
        del charToString[c]
        seen.remove(t)
      return False
    return isMatch(0, 0, {}, set())

