 class Solution:
  def countSmaller(self, nums: List[int]) -> List[int]:
    ans = []
    ranks = collections.Counter()
    self._getRanks(nums, ranks)
    tree = FenwickTree(len(ranks))
    for num in reversed(nums):
      ans.append(tree.get(ranks[num] - 1))
      tree.update(ranks[num], 1)
    return ans[::-1]
  def _getRanks(self, nums: List[int], ranks: Dict[int, int]) -> None:
    rank = 0
    for num in sorted(set(nums)):
      rank += 1
      ranks[num] = rank

class Solution:
  def removeDuplicateLetters(self, s: str) -> str:
    ans = []
    count = collections.Counter(s)
    used = [False] * 26
    for c in s:
      count[c] -= 1
      if used[ord(c) - ord('a')]:
        continue
      while ans and ans[-1] > c and count[ans[-1]] > 0:
        used[ord(ans[-1]) - ord('a')] = False
        ans.pop()
      ans.append(c)
      used[ord(ans[-1]) - ord('a')] = True
    return ''.join(ans)

class Solution:
  def shortestDistance(self, grid: List[List[int]]) -> int:
    m = len(grid)
    n = len(grid[0])
    dirs = [0, 1, 0, -1, 0]
    nBuildings = sum(a == 1 for row in grid for a in row)
    ans = math.inf
    dist = [[0] * n for _ in range(m)]
    reachCount = [[0] * n for _ in range(m)]
    def bfs(row: int, col: int) -> bool:
      q = collections.deque([(row, col)])
      seen = {(row, col)}
      depth = 0
      seenBuildings = 1
      while q:
        depth += 1
        for _ in range(len(q)):
          i, j = q.popleft()
          for k in range(4):
            x = i + dirs[k]
            y = j + dirs[k + 1]
            if x < 0 or x == m or y < 0 or y == n:
              continue
            if (x, y) in seen:
              continue
            seen.add((x, y))
            if not grid[x][y]:
              dist[x][y] += depth
              reachCount[x][y] += 1
              q.append((x, y))
            elif grid[x][y] == 1:
              seenBuildings += 1
      return seenBuildings == nBuildings
    for i in range(m):
      for j in range(n):
        if grid[i][j] == 1:
          if not bfs(i, j):
            return -1
    for i in range(m):
      for j in range(n):
        if reachCount[i][j] == nBuildings:
          ans = min(ans, dist[i][j])
    return -1 if ans == math.inf else ans

class Solution:
  def maxProduct(self, words: List[str]) -> int:
    ans = 0
    def getMask(word: str) -> int:
      mask = 0
      for c in word:
        mask |= 1 << ord(c) - ord('a')
      return mask
    masks = [getMask(word) for word in words]
    for i in range(len(words)):
      for j in range(i):
        if not (masks[i] & masks[j]):
          ans = max(ans, len(words[i]) * len(words[j]))
    return ans

class Solution:
  def bulbSwitch(self, n: int) -> int:
    return int(sqrt(n))

class Solution:
  def generateAbbreviations(self, word: str) -> List[str]:
    ans = []
    def getCountString(count: int) -> str:
      return str(count) if count > 0 else ''
    def dfs(i: int, count: int, path: List[str]) -> None:
      if i == len(word):
        ans.append(''.join(path) + getCountString(count))
        return
      dfs(i + 1, count + 1, path)
      path.append(getCountString(count) + word[i])
      dfs(i + 1, 0, path)
      path.pop()
    dfs(0, 0, [])
    return ans

class Solution:
  def coinChange(self, coins: List[int], amount: int) -> int:
    dp = [0] + [amount + 1] * amount
    for coin in coins:
      for i in range(coin, amount + 1):
        dp[i] = min(dp[i], dp[i - coin] + 1)
    return -1 if dp[amount] == amount + 1 else dp[amount]

class Solution:
  def countComponents(self, n: int, edges: List[List[int]]) -> int:
    ans = 0
    graph = [[] for _ in range(n)]
    seen = set()
    for u, v in edges:
      graph[u].append(v)
      graph[v].append(u)
    def bfs(node: int, seen: Set[int]) -> None:
      q = collections.deque([node])
      seen.add(node)
      while q:
        u = q.pop()
        for v in graph[u]:
          if v not in seen:
            q.append(v)
            seen.add(v)
    for i in range(n):
      if i not in seen:
        bfs(i, seen)
        ans += 1
    return ans

class Solution:
  def maxSubArrayLen(self, nums: List[int], k: int) -> int:
    ans = 0
    prefix = 0
    prefixToIndex = {0: -1}
    for i, num in enumerate(nums):
      prefix += num
      target = prefix - k
      if target in prefixToIndex:
        ans = max(ans, i - prefixToIndex[target])
      if prefix not in prefixToIndex:
        prefixToIndex[prefix] = i
    return ans

class Solution:
  def isPowerOfThree(self, n: int) -> bool:
