class Solution:
  def reverseVowels(self, s: str) -> str:
    charList = list(s)
    vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'}
    l = 0
    r = len(s) - 1
    while l < r:
      while l < r and charList[l] not in vowels:
        l += 1
      while l < r and charList[r] not in vowels:
        r -= 1
      charList[l], charList[r] = charList[r], charList[l]
      l += 1
      r -= 1
    return ''.join(charList)

class MovingAverage:
  def __init__(self, size: int):
    self.size = size
    self.sum = 0
    self.q = collections.deque()
  def next(self, val: int) -> float:
    if len(self.q) == self.size:
      self.sum -= self.q.popleft()
    self.sum += val
    self.q.append(val)
    return self.sum / len(self.q)

class TicTacToe:
  def __init__(self, n: int):
    self.n = n
    self.rows = [0] * n
    self.cols = [0] * n
    self.diag = 0
    self.antiDiag = 0
  def move(self, row: int, col: int, player: int) -> int:
    toAdd = 1 if player == 1 else -1
    target = self.n if player == 1 else -self.n
    if row == col:
      self.diag += toAdd
      if self.diag == target:
        return player
    if row + col == self.n - 1:
      self.antiDiag += toAdd
      if self.antiDiag == target:
        return player
    self.rows[row] += toAdd
    if self.rows[row] == target:
      return player
    self.cols[col] += toAdd
    if self.cols[col] == target:
      return player
    return 0

class Solution:
