class Solution:
  def calcEquation(self, equations: List[List[str]], values: List[float], queries: List[List[str]]) -> List[float]:
    ans = []
    graph = collections.defaultdict(dict)
    for (A, B), value in zip(equations, values):
      graph[A][B] = value
      graph[B][A] = 1 / value
    def devide(A: str, C: str, seen: Set[str]) -> float:
      if A == C:
        return 1.0
      seen.add(A)
      for B, value in graph[A].items():
        if B in seen:
          continue
        res = devide(B, C, seen)
        if res > 0:
          return value * res
      return -1.0
    for A, C in queries:
      if A not in graph and C not in graph:
        ans.append(-1.0)
      else:
        ans.append(devide(A, C, set()))
    return ans

class Solution:
  def findNthDigit(self, n: int) -> int:
    def getDigit(num: int, pos: int, digitSize: int):
      if pos == 0:
        return num % 10
      for _ in range(digitSize - pos):
        num //= 10
      return num % 10
    digitSize = 1
    startNum = 1
    count = 9
    while digitSize * count < n:
      n -= digitSize * count
      digitSize += 1
      startNum *= 10
      count *= 10
    targetNum = startNum + (n - 1) // digitSize
    pos = n % digitSize
    return getDigit(targetNum, pos, digitSize)

class Solution:
  def canCross(self, stones: List[int]) -> bool:
    n = len(stones)
    dp = [[False] * (n + 1) for _ in range(n)]
    dp[0][0] = True
    for i in range(1, n):
      for j in range(i):
        k = stones[i] - stones[j]
        if k > n:
          continue
        for x in (k - 1, k, k + 1):
          if 0 <= x <= n:
            dp[i][k] |= dp[j][x]
    return any(dp[-1])

class Solution:
  def longestPalindrome(self, s: str) -> int:
    ans = 0
    count = collections.Counter(s)
    for c in count.values():
      ans += c if c % 2 == 0 else c - 1
    hasOddCount = any(c % 2 == 1 for c in count.values())
    return ans + hasOddCount

class Solution:
  def splitArray(self, nums: List[int], m: int) -> int:
    n = len(nums)
    prefix = [0] + list(itertools.accumulate(nums))
    @functools.lru_cache(None)
    def dp(i: int, k: int) -> int:
      if k == 1:
        return prefix[i]
      ans = math.inf
      for j in range(k - 1, i):
        ans = min(ans, max(dp(j, k - 1), prefix[i] - prefix[j]))
      return ans
    return dp(n, m)

class Solution:
  def minAbbreviation(self, target: str, dictionary: List[str]) -> str:
    m = len(target)
    def getMask(word: str) -> int:
      mask = 0
      for i, c in enumerate(word):
        if c != target[i]:
          mask |= 1 << m - 1 - i
      return mask
    masks = [getMask(word) for word in dictionary if len(word) == m]
    if not masks:
      return str(m)
    abbrs = []
    def getAbbr(cand: int) -> str:
      abbr = ''
      replacedCount = 0
      for i, c in enumerate(target):
        if cand >> m - 1 - i & 1:
          if replacedCount:
            abbr += str(replacedCount)
          abbr += c
          replacedCount = 0
        else:
          replacedCount += 1
      if replacedCount:
        abbr += str(replacedCount)
      return abbr
    for cand in range(2**m):
      if all(cand & mask for mask in masks):
        abbr = getAbbr(cand)
        abbrs.append(abbr)
    def getAbbrLen(abbr: str) -> int:
      abbrLen = 0
      i = 0
      j = 0
      while i < len(abbr):
        if abbr[j].isalpha():
          j += 1
        else:
          while j < len(abbr) and abbr[j].isdigit():
            j += 1
        abbrLen += 1
        i = j
      return abbrLen
    return min(abbrs, key=lambda x: getAbbrLen(x))

