    n = len(combined)
    i = 0
    for _ in range(rows):
      i += cols
      if combined[i % n] == ' ':
        i += 1
      else:
        while i > 0 and combined[(i - 1) % n] != ' ':
          i -= 1
    return i // n

class Solution:
  def findMaximumXOR(self, nums: List[int]) -> int:
    maxNum = max(nums)
    if maxNum == 0:
      return 0
    maxBit = int(math.log2(maxNum))
    ans = 0
    mask = 0
    for i in range(maxBit, -1, -1):
      mask |= 1 << i
      prefixes = set([num & mask for num in nums])
      candidate = ans | 1 << i
      for prefix in prefixes:
        if prefix ^ candidate in prefixes:
          ans = candidate
          break
    return ans

class Solution:
  def originalDigits(self, s: str) -> str:
