    islands = 0
    neighbors = 0
    for i in range(m):
      for j in range(n):
        if grid[i][j] == 1:
          islands += 1
          if i + 1 < m and grid[i + 1][j] == 1:
            neighbors += 1
          if j + 1 < n and grid[i][j + 1] == 1:
            neighbors += 1
    return islands * 4 - neighbors * 2

class Solution:
  def minTransfers(self, transactions: List[List[int]]) -> int:
    balance = [0] * 21
    for u, v, amount in transactions:
      balance[u] -= amount
      balance[v] += amount
    debt = [b for b in balance if b]
    def dfs(s: int) -> int:
      while s < len(debt) and not debt[s]:
        s += 1
      if s == len(debt):
        return 0
      ans = math.inf
      for i in range(s + 1, len(debt)):
        if debt[i] * debt[s] < 0:
          debt[i] += debt[s]
          ans = min(ans, 1 + dfs(s + 1))
          debt[i] -= debt[s]
      return ans
    return dfs(0)

class Solution:
  def isConvex(self, points: List[List[int]]) -> bool:
    def getCross(p: List[int], q: List[int], r: List[int]):
      return (q[0] - p[0]) * (r[1] - p[1]) - (q[1] - p[1]) * (r[0] - p[0])
    sign = 0
    for i in range(len(points)):
      cross = getCross(points[i - 2], points[i - 1], points[i])
      if cross == 0:
        continue
      if sign == 0:
        sign = cross
      elif cross * sign < 0:
        return False
    return True

class Solution:
