class Solution:
  def findMinStep(self, board: str, hand: str) -> int:
    def deDup(board):
      start = 0
      for i, c in enumerate(board):
        if c != board[start]:
          if i - start >= 3:
            return deDup(board[:start] + board[i:])
          start = i
      return board
    @functools.lru_cache(None)
    def dfs(board: str, hand: str):
      board = deDup(board)
      if board == '#':
        return 0
      boardSet = set(board)
      hand = ''.join(h for h in hand if h in boardSet)
      if not hand:
        return math.inf
      ans = math.inf
      for i in range(len(board)):
        for j, h in enumerate(hand):
          newHand = hand[:j] + hand[j + 1:]
          newBoard = board[:i] + h + board[i:]
          ans = min(ans, 1 + dfs(newBoard, newHand))
      return ans
    ans = dfs(board + '#', hand)
    if ans == math.inf
    return -1
    else ans

class Solution:
  def hasPath(self, maze: List[List[int]], start: List[int], destination: List[int]) -> bool:
    m = len(maze)
    n = len(maze[0])
    dirs = [0, 1, 0, -1, 0]
    q = collections.deque([(start[0], start[1])])
    seen = {(start[0], start[1])}
    def isValid(x: int, y: int) -> bool:
      return 0 <= x < m and 0 <= y < n and maze[x][y] == 0
    while q:
      i, j = q.popleft()
      for k in range(4):
        x = i
        y = j
        while isValid(x + dirs[k], y + dirs[k + 1]):
          x += dirs[k]
          y += dirs[k + 1]
        if [x, y] == destination:
          return True
        if (x, y) in seen:
          continue
        q.append((x, y))
        seen.add((x, y))
    return False

class Solution:
  def findSubsequences(self, nums: List[int]) -> List[List[int]]:
    ans = []
    def dfs(s: int, path: List[int]) -> None:
      if len(path) > 1:
        ans.append(path)
      used = set()
      for i in range(s, len(nums)):
        if nums[i] in used:
          continue
        if not path or nums[i] >= path[-1]:
          used.add(nums[i])
          dfs(i + 1, path + [nums[i]])
    dfs(0, [])
    return ans

class Solution:
  def findTargetSumWays(self, nums: List[int], target: int) -> int:
    summ = sum(nums)
    if summ < abs(target) or (summ + target) & 1:
      return 0
    def knapsack(target: int) -> int:
      dp = [1] + [0] * summ
      for num in nums:
        for j in range(summ, num - 1, -1):
          dp[j] += dp[j - num]
      return dp[target]
    return knapsack((summ + target) // 2)

class Solution:
